mapper.select:
    select rno, bno, writer, content, regDate
    from tbl_reply
	where bno = #{bno}

select rno, bno, writer, content, regDate
from tbl_reply
where bno = #{bno} and rno = #{rno}

mapper.update:
	update tbl_reply set
        writer = #{writer},
        content = #{content}
    where rno = #{rno} and bno = #{bno}
